INSERT INTO `tbl_page` (`id`, `title`, `body`, `name`) VALUES
(1, 'EAMS-TANZANIA', '<p>\r\n	The EAMS-TANZANIA system is for the Ministry of East African Cooperation (MEAC) for monitoring the implementation of the Summit and Council Decisions; the Common Market Protocol; and the 4th EAC Development Strategy.</p>\r\n<p>\r\n	<strong>Core Features </strong></p>\r\n<ul>\r\n	<li>\r\n		EAC integration decisions and directives updates and progress.</li>\r\n	<li>\r\n		Ministry&rsquo;s Strategic Plan tracking of progress of outputs.</li>\r\n	<li>\r\n		EAC outcomes for Tanzania based on selected outcome indicators</li>\r\n</ul>\r\n', 'home_page');
